class Solution(object):
    def lengthOfLIS(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        dp = [1 for i in range(len(nums))]
        max = 1
        for i in range(0, len(nums)-1):
            for j in range(0, i):
                if nums[i] >= nums[j] and (dp[j] + 1) > dp[i]:
                    dp[i] = (dp[j] + 1)
                if max < dp[i]:
                    max = dp[i]
        return max

    #寻找第一个大于等于target的位置
    def binarySearch(self, dp, target):
        low = 0
        high = len(dp) - 1
        mid = -1
        ans = -1
        while low <= high:
            mid = (low + high) // 2
            if dp[mid] < target:
                low = mid + 1
            else:
                high = mid - 1
                ans = mid
        return ans

    def lis2(self, nums):
        if len(nums) == 0:
            return 0
        dp = []
        dp.append(nums[0])
        for i in range(1, len(nums)):
            if nums[i] > dp[-1]:
                dp.append(nums[i])
            else:
                targetIndex = self.binarySearch(dp, nums[i])
                dp[targetIndex] = nums[i]
        return len(dp)

